DROP DATABASE IF EXISTS ecommercestore;
CREATE DATABASE ecommercestore;
use ecommercestore;

CREATE TABLE customers(
customer_id INT NOT NULL PRIMARY KEY auto_increment,
first_name VARCHAR(50) NOT NULL, 
last_name VARCHAR(50) NOT NULL,
birth_date DATE DEFAULT NULL,
phone VARCHAR(50) NOT NULL,
email VARCHAR(50) DEFAULT NULL,
street_address VARCHAR(50) NOT NULL,
city VARCHAR(50) NOT NULL, 
state VARCHAR(50) NOT NULL,
points INT );


CREATE TABLE membership (
customer_id INT NOT NULL PRIMARY KEY,
membership_type VARCHAR(50) DEFAULT NULL CONSTRAINT CC1 CHECK (membership_type IN ('silver', 'gold', 'platinum')), 
FOREIGN KEY (customer_id) REFERENCES customers(customer_id) ON DELETE CASCADE ON UPDATE CASCADE );


CREATE TABLE shippers (
shipper_id SMALLINT NOT NULL PRIMARY KEY AUTO_INCREMENT, 
shipper_name VARCHAR(50) NOT NULL );



CREATE TABLE orders (
order_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, 
order_date DATE DEFAULT NULL, 
status INT NOT NULL CONSTRAINT CC2 CHECK (status IN ('1', '2', '3')) ,
shipper_id SMALLINT DEFAULT NULL  ,
shipped_date DATE DEFAULT NULL, 
FOREIGN KEY (shipper_id) REFERENCES shippers(shipper_id) ON DELETE CASCADE ON UPDATE CASCADE );



CREATE TABLE products (
product_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
product_name VARCHAR(50) NOT NULL,
quantity_in_stock INT NOT NULL,
unit_price FLOAT NOT NULL
);



CREATE TABLE order_items (
order_id INT NOT NULL , 
product_id INT NOT NULL ,
quantity INT NOT NULL, 
unit_price FLOAT NOT NULL,
PRIMARY KEY(order_id, product_id),
FOREIGN KEY (order_id) REFERENCES orders(order_id) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (product_id) REFERENCES products(product_id) ON DELETE CASCADE ON UPDATE CASCADE  );




drop table order_payments_details;
CREATE TABLE order_payments_details (
customer_id INT NOT NULL,
order_id INT NOT NULL,
payment_id INT NOT NULL ,
PRIMARY KEY (customer_id, order_id, payment_id),
FOREIGN KEY (customer_id) REFERENCES customers(customer_id) ON DELETE CASCADE ON UPDATE CASCADE,
FOREIGN KEY (order_id) REFERENCES orders(order_id) ON DELETE CASCADE ON UPDATE CASCADE ,
FOREIGN KEY (payment_id) REFERENCES payments(payment_id) ON DELETE CASCADE ON UPDATE CASCADE 
);


CREATE TABLE payment_method (
payment_method_id INT NOT NULL PRIMARY KEY, 
mode_of_payment VARCHAR(50) NOT NULL ); 

CREATE TABLE payments (
payment_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
payment_date DATE NOT NULL, 
total_amount FLOAT NOT NULL,
payment_method_id INT NOT NULL,
FOREIGN KEY (payment_method_id) REFERENCES payment_method(payment_method_id) ON DELETE CASCADE ON UPDATE CASCADE );


